comment on table dvh_fam_bt.fam_bt_fagsak is '#NAVN Fagsak #INNHOLD Tabellen inneholder barnetrygd vedtaksinformasjon.';
comment on column dvh_fam_bt.fam_bt_fagsak.pk_bt_fagsak is '#NAVN Primær nøkkel #INNHOLD Primær nøkkel, en unik id for hver rad (vedtak), autogenerert av dvh_fambt_kafka.hibernate_sequence.nextval.';
comment on column dvh_fam_bt.fam_bt_fagsak.fk_bt_person is '#NAVN Fremmednøkkel #INNHOLD Fremmednøkkel til FAM_BT_PERSON.';
comment on column dvh_fam_bt.fam_bt_fagsak.fk_bt_meta_data is '#NAVN Fremmednøkkel #INNHOLD Fremmednøkkel til FAM_BT_META_DATA.';
comment on column dvh_fam_bt.fam_bt_fagsak.fagsak_id is '#NAVN FagsakId #INNHOLD Barnetrygd fagsak_id fra vedtaksystem.';
comment on column dvh_fam_bt.fam_bt_fagsak.behandlings_id is '#NAVN BehandlingsId #INNHOLD Barnetrygd behandlings_id fra vedtaksystem. Kan identifisere versjon av fagsak.';
comment on column dvh_fam_bt.fam_bt_fagsak.tidspunkt_vedtak is '#NAVN Vedtaks tidspunkt #INNHOLD Tidspunkt for når vedtaket er fattet.';
